gsap.config({
    nullTargetWarn: !1
});
var M = {
    data: null,
    init: function() {
        M.loadData()
    },
    loadData: function() {
        var a = new XMLHttpRequest;
        a.onreadystatechange = function() {
            if (200 === a.status && 4 === a.readyState) {
                var d = JSON.parse(a.responseText);
                M.data = d
            }
        }
        ;
        a.open("post", "./data.json");
        a.send()
    }
}
var V = {
    hero: null,
    projects: null,
    main: null,
    init: function() {
        V.loader()
    },
    loader: function() {
        CustomEase.create("easeOutExpo", "0.16, 1, 0.3, 1");
        CustomEase.create("easeInExpo", "0.7, 0, 0.84, 0");
        CustomEase.create("strongEaseInOut", "0.08, 0.82, 0.17, 1");
        CustomEase.create("strongEaseInLinearOut", "0.08, 0.82, 1, 1");
        var a = {
            value: 0
        };
        gsap.timeline({}).to(".loader-line", {
            ease: "easeOutExpo",
            duration: 4.5, //change the duration of loading screen 2
        },).to(".black-div", {
            translateY: "-90px",
            ease: "strongEaseInOut",
            duration: 2,
        }, 0.5).to(".black-div-two", {
            translateY: "-90px",
            ease: "strongEaseInOut",
            duration: 2,
        }, 3.5).to(".loader", {
            opacity: 0,
            ease: "easeOutExpo",
            onComplete: function() {
                document.querySelector(".loader").remove()
            }
        }, 4).to([".logo", ".availability"], {
            opacity: 1,
            ease: "easeInExpo"
        }, 4).to(".hero-line", {
            x: 0,
            y: 0,
            z: 0,
            ease: "easeOutExpo",
            duration: 1, //time before animation starts1
            stagger: {
                each: 0.1 //time for each line 0.1
            }
        }, 4)
    }
}
var App = {
    init: function() {
        M.init();
        V.init()
    }
};
window.addEventListener("load", function() {
    App.init()
});

document.addEventListener("DOMContentLoaded", function() {
    CustomEase.create("easeOutExpo", "0.16, 1, 0.3, 1");
    CustomEase.create("easeInExpo", "0.7, 0, 0.84, 0");
    CustomEase.create("strongEaseInOut", "0.08, 0.82, 0.17, 1");
    CustomEase.create("strongEaseInLinearOut", "0.08, 0.82, 1, 1");
    var projAClick = document.getElementById("proj-a-click");
    var projATitle = document.getElementById("proj-a-title");
    var projAImg = document.getElementById("proj-a-img");
    var tlProjAEnter = gsap.timeline({ paused: true });
    var tlProjALeave = gsap.timeline({ paused: true });
    var tlLeavePage = gsap.timeline({ paused: true });

    tlLeavePage.to([".logo", ".availability"], {
        opacity: 0,
        ease: "easeInExpo"
    }).to(".hero-line", {
        x: 0,
        y: 2000,
        z: 0,
        ease: "easeInExpo",
        duration: 1,
        stagger: {
            each: 0.1
        }
    }).to({}, {
        duration: 1,
        onComplete: function() {
            window.location.href = "projA.html";
        }
    });

    tlProjAEnter.to(".proj-a-line", {
        x: 0,
        y: 0,
        z: 0,
        ease: "easeOutExpo",
        duration: 0.5,
        stagger: {
            each: 0.1
        }
    }).to({}, {
        duration: 0,
        onComplete: function() {
            projAImg.style.transitionDelay = "0.5s";
        }
    })

    tlProjALeave.to(".proj-a-line", {
        x: 0,
        y: 2000,
        z: 0,
        ease: "easeInExpo",
        duration: 0.5,
        stagger: {
            each: 0.1
        }
    }).to({}, {
        duration: 0,
        onComplete: function() {
            projAImg.style.transitionDelay = "";
        }
    });



    projAClick.addEventListener("mouseenter", function() {
        tlProjAEnter.restart()
    });
    projAClick.addEventListener("mouseleave", function() {
        tlProjALeave.restart()
    });
    projAClick.addEventListener("click", function() {
        tlLeavePage.restart()
    });
})